pragma solidity ^0.5.16;

contract Charity {
    address payable public rep;
    string public name;
    string public description;
    string public category;

    //uint256 current;

    mapping(address=>uint) public donors;

    event donationReceived(address donor, uint amount);

    event donationSentToTarget(address recipient);

    constructor() public {
    }

    function setup(address payable givenRep,
        string memory givenName,
        string memory givenCategory,
        string memory givenDescription
    ) public {
        rep = givenRep;
        name = givenName;
        description = givenDescription;
        category = givenCategory;
    }

    function donate() external payable returns (bool) {
        require(msg.sender != rep);
        donors[msg.sender] = donors[msg.sender] + msg.value;
        //current += msg.value;
        //payToWallet();
        if(rep.send(msg.value)) {
            emit donationSentToTarget(rep);
            emit donationReceived(msg.sender, msg.value);
            return true;
        }
        return false;
    }
}
